C++Python <pre><code>constexpr int N = 1000 * 1000;

double simpson_integration(double a, double b) {
  double h = (b - a) / N;
  double s = f(a) + f(b);
  for (int i = 1; i &lt;= N - 1; ++i) {
    double x = a + h * i;
    s += f(x) * ((i &amp; 1) ? 4 : 2);
  }
  s *= h / 3;
  return s;
}
</code></pre> <pre><code>N = 1000 * 1000


def simpson_integration(a, b):
    h = (b - a) / N
    s = f(a) + f(b)
    for i in range(1, N):
        x = a + h * i
        if i &amp; 1:
            s = s + f(x) * 4
        else:
            s = s + f(x) * 2
    s = s * (h / 3)
    return s
</code></pre>

C++Python <pre><code>double sqrt_newton(double n) {
  constexpr static double eps = 1E-15;
  double x = 1;
  while (true) {
    double nx = (x + n / x) / 2;
    if (abs(x - nx) &lt; eps) break;
    x = nx;
  }
  return x;
}
