C++Python <pre><code>int isqrt_newton(int n) {
  int x = 1;
  bool decreased = false;
  for (;;) {
    int nx = (x + n / x) &gt;&gt; 1;
    if (x == nx || (nx &gt; x &amp;&amp; decreased)) break;
    decreased = nx &lt; x;
    x = nx;
  }
  return x;
}
